<script type="text/javascript">
	function show_popup(i) {
		if (i === 2) {
			w = window.innerWidth;
			if (w > 640) {
				$('.popup' + i).css('width', '550px !important');
			}
		}
		$('.popup'+i).show('slow');
	}

	$(document).ready(function () {

		//if (window.location.pathname === '/') {
			show_popup(1);
		//}

		$('.home-popup .close-btn i').on('click', function () {
			$('.home-popup').hide('slow');
		});

		width = window.innerWidth;
		if (width > 767) {
			// $('.footer-popup').show('slow');
		}else{
			$('.remove-br').remove();
		}

		$('.footer-popup .close').click(function () {
			$('.footer-popup').hide('slow');
			// console.log('ss');
		});

		/*if (window.location.pathname !== '/') {
			console.log(window.location.pathname);
			$('.float-news').css('right','-420px').addClass('closed');
			$('.hamburger').toggleClass('open');
		}*/

		$('.float-news .hamburger-wrapper').click(function () {

			if ($(this).closest('.float-news').hasClass('closed')) {

				$(this).find('.hamburger').toggleClass('open');
				$(this).closest('.float-news').animate({right: '0'}, 'slow').toggleClass('closed');

			} else {

				$(this).find('.hamburger').toggleClass('open');
				$(this).closest('.float-news').animate({right: '-420px'}, 'slow').toggleClass('closed');

			}
		});
	});
</script>
